<template>
  <div class="home">
    <img alt="Vue logo" :src="imageUrl" /><br>
    <input type="number" v-model="nb" v-if="showInput">
    <p>{{ multiplication }}</p>
    <button @click="changeInputVisibility()">Change visibility</button>
    <ul>
      <li v-for="name in names" :key="name">{{ name }}</li>
    </ul>

    <h1>Mes super héros préférés</h1>
    <div class="location-contain">
      <div class="locations" v-for="location in locations" :key="location.name">
        <card :item="location" />
      </div>
    </div>
  </div>
</template>

<script lang="ts">
import { Component, Vue } from "vue-property-decorator";
import Card from "@/components/Card.vue"; // @ is an alias to /src
import { CardItem } from '@/models/card-item';

@Component({
  components: {
    Card
  }
})
export default class Home extends Vue {
  nb = 0;
  showInput = true;
  names: string[] = [];
  imageUrl = "https://media1.giphy.com/media/WV9Xx9itqEIu8GQloO/giphy.gif?cid=ecf05e4775a7936d26aeb4ae8f7b783444bc39ca9d2adda5&rid=giphy.gif";
  locations: CardItem[] = [];

  created(): void {
    this.names = ["titi", "tata", "tutu"];
    this.locations = [
      {
        name: "Batman",
        description: "Bruce Wayne, alias Batman, est un super-héros de fiction appartenant à l'univers de DC Comics.",
        imgUrl: "data:image/png;base64,"
      },
      {
        name: "Deadpool",
        description: "Wade Wilson, alias Deadpool est un anti-héros évoluant dans l'univers Marvel de la maison d'édition Marvel Comics.",
        imgUrl: "data:image/jpeg;base64,"
      }
    ]
  }

  get multiplication(): number{
    return this.nb * 100;
  }

  changeInputVisibility(){
    this.showInput = !this.showInput;
  }
}
</script>
